package com.opencast.SpringSample;

public class FileWriter implements IFileWriter {
    String filename
    String initialText
    private ArrayList<String> messageData
    private ArrayList<String> dataToWrite = new ArrayList<String>();

    public String getFilename() {
        return filename
    }

    public String getInitialText() {
        return initialText
    }

    public void setMessageData(ArrayList<String> messageData) {
        this.messageData  = messageData
    }

    public ArrayList<String> getDataToWrite() {
        return dataToWrite
    }

    private boolean processFilter(String item) {
        return item.contains('write')
    }

    private String processMapper(String item) {
        return "${ item } PROCESSED"
    }

    public List<String> processData(List<String> messageData) {
        List<String> dataToWrite = new ArrayList<String>();
        messageData.each {
            if (processFilter(it)) {
                dataToWrite.add(processMapper(it))
            }
        }
        dataToWrite
    }
}
